list.definition:
//
// Battery Loading Level Current
//

from(bucket: "Fronius")
  |> range(start: -1m)
  |> filter(fn: (r) => (r["_measurement"] == "Battery" and r._field == "StateOfCharge_Relative"))
  |> last()

